mapper.select:
SELECT * FROM( SELECT AA.*, CEIL(ROW_NUMBER() OVER(ORDER BY ROWNUM)/#{pageSize}) PAGENO, COUNT(ROWNUM) OVER() AS totalCount FROM( SELECT ROWNUM, A.hm_empnum, E.cd_name AS hm_deptnum, D.cd_name AS hm_teamnum, A.hm_name, A.hm_birth, A.hm_hp, A.hm_extensionnum, A.hm_email, A.hm_hiredate, A.hm_picture, C.cd_name AS hm_position, B.cd_name AS hm_duty, A.hm_employmenttype FROM HUMAN_MEMBER A, CODE B, CODE C,CODE D,CODE E WHERE A.hm_duty=B.cd_code AND A.hm_position=C.cd_code AND A.hm_teamnum=D.cd_code AND SUBSTR(A.hm_deptnum,1,2)=E.cd_code AND A.hm_deptnum!='98' AND A.hm_deleteyn='Y'
hm_name LIKE '%'||#{keyword}||'%'
AND hm_deptnum LIKE '%'||#{keyword}||'%'
AND hm_position LIKE '%'||#{keyword}||'%'
AND hm_employmenttype LIKE '%'||#{keyword}||'%'
)AA ) WHERE PAGENO = #{curPage}

SELECT * FROM( SELECT AA.*, CEIL(ROW_NUMBER() OVER(ORDER BY ROWNUM)/#{pageSize}) PAGENO, COUNT(ROWNUM) OVER() AS totalCount FROM( SELECT ROWNUM, A.hm_empnum, A.hm_deptnum, A.hm_teamnum, A.hm_name, A.hm_birth, A.hm_extensionnum, A.hm_email, A.hm_hp, A.hm_hiredate, C.cd_name AS hm_position, B.cd_name AS hm_duty, A.hm_employmenttype FROM HUMAN_MEMBER A, CODE B, CODE C WHERE HM_DELETEYN='Y' AND hm_deptnum LIKE '%'||#{hm_deptnum}||'%' AND A.hm_duty=B.cd_code AND A.hm_position=C.cd_code
hm_name LIKE '%'||#{keyword}||'%'
AND hm_position LIKE '%'||#{keyword}||'%'
AND hm_deleteYN LIKE '%'||#{keyword}||'%'
AND hm_employmenttype LIKE '%'||#{keyword}||'%'
)AA ) WHERE PAGENO = #{curPage}

SELECT * FROM( SELECT AA.*, CEIL(ROW_NUMBER() OVER(ORDER BY ROWNUM)/#{pageSize}) PAGENO, COUNT(ROWNUM) OVER() AS totalCount FROM( SELECT ROWNUM, A.hm_empnum, A.hm_deptnum, A.hm_teamnum, A.hm_name, A.hm_birth, A.hm_extensionnum, A.hm_email, A.hm_hiredate, A.hm_hp, C.cd_name AS hm_position, B.cd_name AS hm_duty, A.hm_deleteYN, A.hm_employmenttype FROM HUMAN_MEMBER A, CODE B, CODE C WHERE A.hm_duty=B.cd_code AND A.hm_position=C.cd_code
hm_name LIKE '%'||#{keyword}||'%'
AND hm_position LIKE '%'||#{keyword}||'%'
AND hm_deleteYN LIKE '%'||#{keyword}||'%'
AND hm_employmenttype LIKE '%'||#{keyword}||'%'
)AA ) WHERE PAGENO = #{curPage}

SELECT * FROM( SELECT AA.*, CEIL(ROW_NUMBER() OVER(ORDER BY ROWNUM)/#{pageSize}) PAGENO, COUNT(hpa_tablenum) OVER() AS totalCount FROM( SELECT ROWNUM, A.hpa_tablenum, A.hpa_empnum, A.hpa_name, A.hpa_appointmentdate, B.cd_name AS hpa_appointment, C.cd_name AS hpa_bposition, D.cd_name AS hpa_bduty, E.cd_name AS hpa_bdept, F.cd_name AS hpa_bteam, G.cd_name AS hpa_aposition, H.cd_name AS hpa_aduty, I.cd_name AS hpa_adept, J.cd_name AS hpa_ateam, A.hpa_contents, A.hpa_insertdate, A.hpa_deleteyn
FROM HUMAN_PERSONAPPT A,CODE B, CODE C,CODE D, CODE E, CODE F, CODE G, CODE H, CODE I, CODE J WHERE A.HPA_EMPNUM=#{hpa_empnum}
AND A.hpa_appointment=B.cd_code AND A.hpa_bposition=C.cd_code AND A.hpa_bduty=D.cd_code AND A.hpa_bdept=E.cd_code AND A.hpa_bteam=F.cd_code AND A.hpa_aposition=G.cd_code AND A.hpa_aduty=H.cd_code AND A.hpa_adept=I.cd_code AND A.hpa_ateam=J.cd_code )AA ) WHERE PAGENO = #{curPage}

SELECT * FROM( SELECT AA.*, CEIL(ROW_NUMBER() OVER(ORDER BY ROWNUM)/#{pageSize}) PAGENO, COUNT(hpa_tablenum) OVER() AS totalCount FROM( SELECT ROWNUM, A.hpa_tablenum, A.hpa_empnum, A.hpa_name, A.hpa_appointmentdate, B.cd_name AS hpa_appointment, C.cd_name AS hpa_bposition, D.cd_name AS hpa_bduty, E.cd_name AS hpa_bdept, F.cd_name AS hpa_bteam, G.cd_name AS hpa_aposition, H.cd_name AS hpa_aduty, I.cd_name AS hpa_adept, J.cd_name AS hpa_ateam, A.hpa_contents, A.hpa_insertdate, A.hpa_deleteyn
FROM HUMAN_PERSONAPPT A,CODE B, CODE C,CODE D, CODE E, CODE F, CODE G, CODE H, CODE I, CODE J WHERE A.hpa_appointment=B.cd_code
hpa_name LIKE '%'||#{keyword}||'%'
AND hpa_empnum LIKE '%'||#{keyword}||'%'
AND A.hpa_bposition=C.cd_code AND A.hpa_bduty=D.cd_code AND A.hpa_bdept=E.cd_code AND A.hpa_bteam=F.cd_code AND A.hpa_aposition=G.cd_code AND A.hpa_aduty=H.cd_code AND A.hpa_adept=I.cd_code AND A.hpa_ateam=J.cd_code
)AA ) WHERE PAGENO = #{curPage}

SELECT * FROM( SELECT AA.*, CEIL(ROW_NUMBER() OVER(ORDER BY ROWNUM)/#{pageSize}) PAGENO, COUNT(ROWNUM) OVER() AS totalCount FROM( select ROWNUM, B.hc_comnum, B.hm_empnum, TO_DATE(NVL(B.hc_date,0),'YYYY-MM-DD') AS hc_date, TO_DATE(NVL(B.hc_workin,0),'HH24MI') AS hc_workin, TO_DATE(NVL(B.hc_workout,0),'HH24MI') AS hc_workout, NVL(B.hc_dayhour,0) AS hc_dayhour, NVL(B.hc_weekhour,0) AS hc_weekhour, NVL(B.hc_lasthour,0) AS hc_lasthour, NVL(B.hc_extraworking,0) AS hc_extraworking, A.cd_name AS hc_tanda
FROM HUMAN_COMMUTE B, CODE A WHERE B.hm_empnum=#{hm_empnum} AND B.hc_tanda=A.cd_code
AND B.hc_date=#{startdate}
AND B.hc_date <= #{enddate} AND B.hc_date >= #{startdate}
ORDER BY A.cd_name, B.hc_comnum DESC )AA ) WHERE PAGENO = #{curPage}

mapper.update:

update human_commute A
 set     A.hc_lasthour = #{hc_lasthour}
        ,A.hc_extraworking = #{hc_extraworking}
        ,A.hc_totalhour = #{hc_totalhour}
 where  A.hm_empnum = #{hm_empnum}
 and    A.hc_comnum = #{hc_comnum}
