mapper.select:
SELECT ap.sm_num, ap.sm_name, ap.sm_hp, ap.sm_email, ap.sm_regularYN, a.cd_name as sm_means, ap.sm_optionalterms, ap.sm_insertdate, ap.sm_updatedate, ap.pageNo, ap.totalCount FROM ( SELECT sm.sm_num, sm.sm_name, sm.sm_hp, sm.sm_email, sm.sm_regularYN, sm.sm_means, sm.sm_optionalterms, sm.sm_insertdate, sm.sm_updatedate, CEIL(ROW_NUMBER() OVER(ORDER BY sm.sm_num)/#{param2.pageSize}) AS pageNo, COUNT(sm_num) OVER() AS totalCount FROM sponsor_member sm WHERE sm.sm_deleteYN = 'Y'
sm.sm_num = #{param1.sm_num}
sm.sm_num LIKE '%'||#{param2.keyword}||'%'
sm.sm_name LIKE '%'||#{param2.keyword}||'%'
sm.sm_insertdate LIKE '%'||#{param2.keyword}||'%'
) ap, code a WHERE ap.pageNo = #{param2.curPage} AND ap.sm_means = a.cd_code

SELECT ap.smc_num, ap.sm_num, a.cd_name as smc_cardcorp, ap.smc_cardnum, ap.smc_cardowner, ap.smc_cardexpired, ap.smc_cardcvc, ap.smc_insertdate, ap.smc_updatedate, ap.pageNo, ap.totalCount FROM ( SELECT smc.smc_num, smc.sm_num, smc.smc_cardcorp, smc.smc_cardnum, smc.smc_cardowner, smc.smc_cardexpired, smc.smc_cardcvc, smc.smc_insertdate, smc.smc_updatedate, CEIL(ROW_NUMBER() OVER(ORDER BY smc.sm_num)/#{param2.pageSize}) AS pageNo, COUNT(sm_num) OVER() AS totalCount FROM sponsor_membercard smc WHERE smc.smc_deleteYN = 'Y'
smc.sm_num = #{param1.sm_num}
sm.sm_num LIKE '%'||#{param2.keyword}||'%'
sm.sm_name LIKE '%'||#{param2.keyword}||'%'
sm.sm_insertdate LIKE '%'||#{param2.keyword}||'%'
) ap, code a WHERE ap.pageNo = #{param2.curPage} AND ap.smc_cardcorp = a.cd_code

SELECT ap.sma_num, ap.sm_num, a.cd_name as sma_bank, ap.sma_accountnum, ap.sma_depositor, ap.sma_insertdate, ap.sma_updatedate, ap.pageNo, ap.totalCount FROM ( SELECT sma.sma_num, sma.sm_num, sma.sma_bank, sma.sma_accountnum, sma.sma_depositor, sma.sma_insertdate, sma.sma_updatedate, CEIL(ROW_NUMBER() OVER(ORDER BY sma.sm_num)/#{param2.pageSize}) AS pageNo, COUNT(sm_num) OVER() AS totalCount FROM sponsor_memberaccount sma WHERE sma.sma_deleteYN = 'Y'
sma.sm_num = #{param1.sm_num}
sma.sm_num LIKE '%'||#{param2.keyword}||'%'
sma.sm_name LIKE '%'||#{param2.keyword}||'%'
sma.sm_insertdate LIKE '%'||#{param2.keyword}||'%'
) ap, code a WHERE ap.pageNo = #{param2.curPage} AND ap.sma_bank = a.cd_code

SELECT ap.sc_num, ap.sc_name, ap.sc_ceo, ap.sc_hp, ap.sc_email, ap.sc_addr, ap.sc_registration, ap.sc_registrationdate, a.cd_name as sc_bizfield, ap.sc_bizcontents, ap.sc_targetamount, ap.sc_amountingstart, ap.sc_amountingend, b.cd_name as sc_bank, ap.sc_accountnum, ap.sc_depositor, ap.sc_deleteYN, ap.sc_insertdate, ap.sc_updatedate, ap.pageNo, ap.totalCount FROM ( SELECT sc.sc_num, sc.sc_name, sc.sc_ceo, sc.sc_hp, sc.sc_email, sc.sc_addr, sc.sc_registration, sc.sc_registrationdate, sc.sc_bizfield, sc.sc_bizcontents, sc.sc_targetamount, sc.sc_amountingstart, sc.sc_amountingend, sc.sc_bank, sc.sc_accountnum, sc.sc_depositor, sc.sc_deleteYN, sc.sc_insertdate, sc.sc_updatedate, CEIL(ROW_NUMBER() OVER(ORDER BY sc.sc_num)/#{param2.pageSize}) AS pageNo, COUNT(sc_num) OVER() AS totalCount FROM sponsor_charity sc WHERE sc.sc_deleteYN = 'Y'
sc.sc_num = #{param1.sc_num}
sc.sc_bizfield LIKE '%'||#{param2.keyword}||'%'
sc.sc_bizcontents LIKE '%'||#{param2.keyword}||'%'
sc.sc_name LIKE '%'||#{param2.keyword}||'%'
) ap, code a, code b WHERE ap.pageNo = #{param2.curPage} AND ap.sc_bizfield = a.cd_code AND ap.sc_bank = b.cd_code
